INSERT INTO `course` (`id`, `department`, `chdepartment`, `code`, `chname`, `teacher`, `description`, `credit`, `fbreview`, `likeit`, `dislikeit`, `availability`, `grade`) VALUES
(1, 'AEU', '電機工程學系', 'AEU0001', '普通物理（一）', '', '1、培育物理相關科技專業人才與師資。 2、養成物理特性分析能力並活用物理知識於理工科技。\n3、開發先知先覺潛力，藉物理方法挖掘、解決問題。', 3, NULL, 0, 0, 0, ''),
(2, 'AEU', '電機工程學系', 'AEU0002', '微積分（一）', '', '讓學生認識微積分的理論與熟悉其計算和應用。單變數課程包括:\n極限、連續、微分、連鎖律、極值問題、積分、微積分學基本定理、微分方程、羅必達規則、瑕積分、無窮級數、泰勒級數。單變數課程包括:\n極限、連續、偏微分、方向導數、梯度、極值問題、重積分。', 3, NULL, 0, 0, 0, ''),
(6, 'AEU', '電機工程學系', 'AEU0003', '數位系統', '', '本課程旨在介紹數位系統及設計與分析的概念, 讓學生認識用於數位系統設計及分析的現代化工具。', 3, NULL, 0, 0, 0, ''),
(10, 'AEU', '電機工程學系', 'AEU0004', '數位系統實驗', '林政宏', '讓學生有機會將數位系統課程所學知識，進行實驗並驗證其正確性。經由作業指派，訓練學生活用所學的能力。', 2, NULL, 0, 0, 1, '沒有限制'),
(17, 'AEU', '電機工程學系', 'AEU0005', '計算機概論', '', '第一部分主要是認識電腦基本觀念、網際網路、與全球資訊網簡介。\n第二部分為本課程重點，著重在學生如何使用高階程式語言設計程式，為將來程式設計進階課程建立良好基礎，主要是C語言程式設計，將介紹程序性語言－C語言的觀念與設計。', 3, NULL, 0, 0, 0, ''),
(18, 'AEU', '電機工程學系', 'AEU0006', '電子學（一）', '', '使學生了解最基本的電子元件和電路。內容包括信號源的種類、運算放大器(OPA)、二極體(diode)、金氧半場效電晶體(MOSFET)、雙極性電晶體(BJT)、以及放大器等主題。', 3, NULL, 0, 0, 0, ''),
(19, 'AEU', '電機工程學系', 'AEU0007', '電子學實驗（一）', '', '使學生了解電子電路系統之基本知識，具備電路系統之維護及設計應用技能，及將來電子業從業人員之專業態度，和瞭解電子元件及應用之發展情形。', 2, NULL, 0, 0, 0, ''),
(20, 'AEU', '電機工程學系', 'AEU0008', '工程數學（一）', '陳瑄易', '本課程含三部分：一、微分方程：微分方程有哪些分類，定義常微分方程以及階數，了解常微分方程的general solution 和\nparticular\nsolution及其初始和邊界條件的定義，除此之外還介紹各種求解微分方程的方法。二、Laplace轉換：介紹Laplace\ntransform 的定義，適用範圍和收斂空間，利用Laplace transform解微分方程，並介紹Laplace\ntransform微分與積分定理。三、介紹矩陣與向量及其運算形式：如何做矩陣行列式的基本運算和性質，學習秩和反矩陣的運算，介紹矩陣的對角化問題的計算和一些特殊矩陣的介紹與應用。', 3, NULL, 0, 0, 1, '沒有限制'),
(21, 'AEU', '電機工程學系', 'AEU0009', '普通物理（二）', '', '讓學生了解電學、磁学與電磁波的關係與特性。一些近代物理觀念如光子、物資波，原子與雷射等．相對論的時空觀念、質能公式等', 3, NULL, 0, 0, 0, ''),
(22, 'AEU', '電機工程學系', 'AEU0010', '微積分（二）', '', '讓學生認識微積分的理論與熟悉其計算和應用。單變數課程包括:\n極限、連續、微分、連鎖律、極值問題、積分、微積分學基本定理、微分方程、羅必達規則、瑕積分、無窮級數、泰勒級數。多變數課程包括:\n極限、連續、偏微分、方向導數、梯度、極值問題、重積分。', 3, NULL, 0, 0, 0, ''),
(23, 'AEU', '電機工程學系', 'AEU0011', '電子學（二）', '蘇崇彥', '使學生了解更複雜的類比與數位積體電路及系統。內容包括單級的積體電路放大器、差動和多級放大器、頻率響應、回授、及運算放大器的內部電路。', 3, NULL, 0, 0, 1, '沒有限制'),
(24, 'AEU', '電機工程學系', 'AEU0012', '電子學實驗（二）', '蔡政翰', '本課程在訓練學生能夠認識基本電子電路。在學習過程中，將先藉由電路模擬軟體(如Hspice)來瞭解電路的設計分析，再經由實驗實作，配合電子學(二)課堂課的學習，進行信號量測與分析。希望藉由此課程，進一步瞭解書本的知識，將自己所學加以融會貫通，有助於往後的學習課程。', 2, NULL, 0, 0, 1, '沒有限制'),
(25, 'AEU', '電機工程學系', 'AEU0013', '程式設計', '', '本科目旨在使學生能以程式設計語言C語言為基礎，於熟習基本程式語法後，再導入C++程式語言。內容著重邏輯思考與程式設計能力之訓練及其在工程上之應用，同時輔以程式設計過程所應注意之程式結構、偵錯、可攜性、軟體工程的基本概念，使修習者得以建立設計程式語言之基礎。', 3, NULL, 0, 0, 0, ''),
(26, 'AEU', '電機工程學系', 'AEU0014', '電路學（一）', '郭建宏', '本課程是電機工程領域之入門課程，在本課程中首要加強電路分析能力，並進而學習如何設計電路之能力。', 3, NULL, 0, 0, 1, '沒有限制'),
(27, 'AEU', '電機工程學系', 'AEU0015', '工程數學（二）', '', '本課程含二部分：一、向量分析：介紹向量的定義，向量微分，及向量積分等。二、傅立葉級數：介紹傅立葉級數的定義，及其計算，介紹傅立葉轉換問題及傅立葉積分介紹與應用。', 3, NULL, 0, 0, 0, ''),
(28, 'AEU', '電機工程學系', 'AEU0016', '計算機結構', '', '讓學生瞭解計算機內部的實際結構與設計 CPU 的知識', 3, NULL, 0, 0, 0, ''),
(29, 'AEU', '電機工程學系', 'AEU0017', '電路學（二）', '', '介紹交流電路與頻率響應的基本原理，以成為日後電路設計與進階課程之基礎。', 3, NULL, 0, 0, 0, ''),
(30, 'AEU', '電機工程學系', 'AEU0018', '工程數學（三）', '', '', 3, NULL, 0, 0, 0, ''),
(31, 'AEU', '電機工程學系', 'AEU0019', '電工實驗', '', '本課程目的在教授學生將理論與實驗相印證，透過電儀表、電工元件等操作為基礎，進行電工相關基礎理論知識及技術的學習。', 2, NULL, 0, 0, 0, ''),
(32, 'AEU', '電機工程學系', 'AEU0020', '電子學（三）', '林群祐', '使學生了解電子電路之重要應用。內容包括濾波器(filter)及調變放大器、信號產生器與波形整型電路、輸出級與功率放大器、CMOS數位邏輯電路、進階MOS及BJT邏輯電路、以及記憶體電路等主題。', 3, NULL, 0, 0, 1, '沒有限制'),
(33, 'AEU', '電機工程學系', 'AEU0021', '感測與轉換', '廖國民', '本課程目的在教授學生基本感測器的原理、轉換器原理及其物理意義，並講解實地測試時的應用方法提升學生執行機電等控制的專業知識與技能\n本課程的實施注重學習者對感測器與轉換器於量測、控制及應用上的目的及方式，建立控制系統週迴授感測的完整概念。同時課程的實施中，理論講述外並配合各式感測實物展示、操作講解與示範等教學活動，進一步提升學生學習興趣與成效。', 3, NULL, 0, 0, 1, ''),
(34, 'AEU', '電機工程學系', 'AEU0022', '資料結構', '高文忠', '資料結構是計算機科學中重要的一門課程，對於有心想學如何寫出好程式的學習者而言，更是必備的基礎能力。課程中將介紹如何在電腦中，將所要處理的資料以結構化方式有效地儲存與取用，以及如何應用這些技巧，提升電腦程式撰寫的能力，藉以提高執行效率，並有效解決問題。', 3, NULL, 0, 0, 1, '沒有限制'),
(35, 'AEU', '電機工程學系', 'AEU0023', '微處理機', '黃奇武', '本課程透過多個實作作業的設計，讓學生充分體會由需求，規格制定，演算法規畫，到實際完成作品，進行測試與版本修改的實際體驗，透過講授、閱讀、討論及問題解決教學與實作方式介紹微處理機的結構／概念，來培養學生對應系所所需的核心能力：\n1.第一階段先複習組成元件，透過HDL語言的模擬與實作，熟悉組成微處理機元件的特性。\n2.第二階段先進行由元件組成微處理機硬體電路的設計/分析；然後透過組合語言軟體程式設計，控制硬體電路', 3, NULL, 0, 0, 1, '沒有限制'),
(36, 'AEU', '電機工程學系', 'AEU0024', '電腦多媒體', '', '', 3, NULL, 0, 0, 0, ''),
(37, 'AEU', '電機工程學系', 'AEU0025', '訊號與系統', '王嘉斌', '學習者能運用訊號與系統的知識設計信號處理的系統.', 3, NULL, 0, 0, 1, '沒有限制'),
(38, 'AEU', '電機工程學系', 'AEU0026', '應用電子專題（一）', '', '訓練學生專題實作能力，提升學生實務技能、培養學生團隊分工合作精神、並藉由競賽來提高學生成品之水準。 本專題課程分二種方式實施： 1.\n基礎學力檢定(專題一)，含硬體能力與軟體能力 2. 實作競賽(專題二、三、四)', 2, NULL, 0, 0, 0, ''),
(39, 'AEU', '電機工程學系', 'AEU0027', '通訊原理（一）', '王嘉斌', 'This course\nprovides the knowledge of fundamental communication systems for\nstudents majoring in applied electronic technology. The course\ncovers Random Process, Continuous-Wave Modulation, Pulse\nModulation, Baseband Pulse Transmission, Signal-Space Analysis, and\nPassband Digital Transmission.', 3, NULL, 0, 0, 1, '沒有限制'),
(40, 'AEU', '電機工程學系', 'AEU0028', '數位信號處理', '', '', 3, NULL, 0, 0, 0, ''),
(41, 'AEU', '電機工程學系', 'AEU0029', '可程式控制', '', '本課程目的在準備學生從事電機與機電等相關控制所需的專業知識，習會藉由可程式控制器達成各種工業控制的目的。\n本課程的實施中，理論講述外並配合各式控制器及週邊感測及控制元件的實物展示、講解等教學活動，加強理論認知的學習成效，進一步提升學生於工程實務應用上的興趣與技能。', 3, NULL, 0, 0, 0, ''),
(42, 'AEU', '電機工程學系', 'AEU0030', '電機機械（一）', '', '本課程目的在教授學生磁路理論、機電能量轉換、變壓器及同步機穩態的原理，並講解實地應用方法提升學生執行機電等控制與驅動的專業知識與技能，並探討工程應用的基本設計概要。\n本課程的實施中，理論講述外並配合各式電機機械的實物展示、講解等教學活動，加強理論認知的學習成效，進一步提升學生於工程實務應用上的興趣與技能。', 3, NULL, 0, 0, 0, ''),
(43, 'AEU', '電機工程學系', 'AEU0031', '編碼理論', '', '', 3, NULL, 0, 0, 0, ''),
(44, 'AEU', '電機工程學系', 'AEU0032', '工業電子學', '', '本課程主要是讓學生瞭解工業電子元件概念及如何使用於實際的工業應用上.', 3, NULL, 0, 0, 0, ''),
(45, 'AEU', '電機工程學系', 'AEU0033', '機器人控制', '', '', 0, NULL, 0, 0, 0, ''),
(46, 'AEU', '電機工程學系', 'AEU0034', '通訊系統電腦模擬', '', '', 3, NULL, 0, 0, 0, ''),
(47, 'AEU', '電機工程學系', 'AEU0035', '隨機程序', '', '', 3, NULL, 0, 0, 0, ''),
(48, 'AEU', '電機工程學系', 'AEU0036', '超大積體電路設計導論', '', '', 0, NULL, 0, 0, 0, ''),
(49, 'AEU', '電機工程學系', 'AEU0037', '資料庫', '', '', 3, NULL, 0, 0, 0, ''),
(50, 'AEU', '電機工程學系', 'AEU0038', '微算機系統', '', '本課程使用C語言，透過Keil uVision及Xilinx\nEDK兩種EDA工具的使用，讓學生瞭解微算機系統概要及相關介面技術，培養學生的軟硬體協同設計能力。', 3, NULL, 0, 0, 0, ''),
(51, 'AEU', '電機工程學系', 'AEU0039', '物件導向程式設計', '', '培養具有良好物件導向程式設計能力的工程師。', 3, NULL, 0, 0, 0, ''),
(52, 'AEU', '電機工程學系', 'AEU0040', '電子儀表', '', '使修習此課的學子們能了解到當前或未來輔助其進行電子電路設計的各式儀表之原理與操作方式，透過觀念講述與實例操作，來獲得電路實際運作的感覺與自行除錯更正的能力。', 3, NULL, 0, 0, 0, ''),
(53, 'AEU', '電機工程學系', 'AEU0041', '電磁學', '', '', 3, NULL, 0, 0, 0, ''),
(54, 'AEU', '電機工程學系', 'AEU0042', '應用電子專題（二）', '郭建宏', '本課程主要在讓學生應用所學，學習簡易電子產品的製作，從類比或數位積體電路，到系統產品的應用都可以是學生製作的目標。重點在使得學生能在學習過程中，資料的搜尋與蓃集，技術的研究與應用討論，教授和學生的互動，同儕之間的學習合作，以便將能在研究或是職場上，都能掌握學習的技巧，和溝通合作的能力。', 2, NULL, 0, 0, 1, '沒有限制'),
(55, 'AEU', '電機工程學系', 'AEU0043', '控制系統', '', '本課程主要在探討控制系統的相關理論及其應用，使學生具備控制系統之數學基礎以深入瞭解控制系統的理論基礎，並應用控制系統的相關理論以解決各類實務問題。', 3, NULL, 0, 0, 0, ''),
(56, 'AEU', '電機工程學系', 'AEU0044', '單晶片控制', '', '本課程主要是讓學生瞭解單晶片硬體架構及軟體指令，進而利用單晶片實現控制系統。', 3, NULL, 0, 0, 0, ''),
(57, 'AEU', '電機工程學系', 'AEU0045', '電機機械（二）', '洪欽銘 陳瑄易', '本課程目的為介紹關於電機機械的基本電路理論、電磁感應原理以及各種運動特性。本學期教學內容將以交流機及特殊電機為主。', 3, NULL, 0, 0, 1, '沒有限制'),
(58, 'AEU', '電機工程學系', 'AEU0046', '通訊原理（二）', '', 'This course\nprovides the knowledge of digital communication systems for\nstudents majoring in applied electronic technology. The course\ncovers “Probability and Random Variables: Review and Notation”,\n“Introduction to Random Processes”, “Linear Filtering of Random\nProcesses”, “Frequency-Domain Analysis of Random Processes in\nLinear Systems”, “Baseband Transmission of Binary Data”, “Coherent\nCommunications”, “Noncoherent Communications”, ”Intersymbol\nInterference”, and “Spread-Spectrum Communications”.', 3, NULL, 0, 0, 0, ''),
(59, 'AEU', '電機工程學系', 'AEU0047', '數據通訊', '', '', 3, NULL, 0, 0, 0, ''),
(60, 'AEU', '電機工程學系', 'AEU0048', '數位信號處理與週邊應用', '', '', 3, NULL, 0, 0, 0, ''),
(61, 'AEU', '電機工程學系', 'AEU0049', '作業系統', '', '', 3, NULL, 0, 0, 0, ''),
(62, 'AEU', '電機工程學系', 'AEU0050', '嵌入式軟體設計', '', '', 3, NULL, 0, 0, 0, ''),
(63, 'AEU', '電機工程學系', 'AEU0051', '電子電路設計', '', '使學子得以透過實際例子了解到電子電路的設計，透過實際的案例，學習到如何將已習得的知識化為務實的應用，得以溫故知新。', 3, NULL, 0, 0, 0, ''),
(64, 'AEU', '電機工程學系', 'AEU0052', '應用電子專題（三）', '', '檢驗學生的專業知識及實作的能力，培養學生設計與研發的能力，使本系之學生在畢業前至少具備基本的職場實務能力。', 2, NULL, 0, 0, 0, ''),
(65, 'AEU', '電機工程學系', 'AEU0053', '類比積體電路設計', '', '', 3, NULL, 0, 0, 0, ''),
(66, 'AEU', '電機工程學系', 'AEU0054', '創意與專利', '', '使學習者具備寫作專利申請書的能力與經驗，並激發學習者的創意與發明潛能。', 3, NULL, 0, 0, 0, ''),
(67, 'AEU', '電機工程學系', 'AEU0055', '人工智慧', '', '', 3, NULL, 0, 0, 0, ''),
(68, 'AEU', '電機工程學系', 'AEU0056', '應用電子專題（四）', '王偉彥', '訓練學生專題實作能力，提升學生實務技能、培養學生團隊分工合作精神、並藉由競賽來提高學生成品之水準。 本專題課程分二種方式實施： 1.\n基礎學力檢定(專題一)，含硬體能力與軟體能力 2. 實作競賽(專題二、三、四)', 2, NULL, 0, 0, 1, '沒有限制'),
(69, 'AEU', '電機工程學系', 'AEU0057', '近代控制', '', '', 0, NULL, 0, 0, 0, ''),
(70, 'AEU', '電機工程學系', 'AEU0058', '數值分析', '', '', 0, NULL, 0, 0, 0, ''),
(71, 'AEU', '電機工程學系', 'AEU0059', '電腦網路', '', '', 0, NULL, 0, 0, 0, ''),
