(77, 'AEU', '電機工程學系', 'AEU0065', '工程機率與統計', '', '本課程主要建立學生對機率理論的基本概念與應用，並側重於機率理論與機率模型在通訊網路工程領域中應用與分析。', 3, NULL, 0, 0, 0, ''),
(78, 'AEU', '電機工程學系', 'AEU0066', '線性代數', '', '本課程主要是讓學生瞭解線性代數之重要概念，以及相關定理證明、運算與應用', 3, NULL, 0, 0, 0, ''),
(79, 'AEU', '電機工程學系', 'AEU0067', '電磁學（一）', '', '本科目旨在培養學生對於電、磁二者的物理觀念及其在電子電機工程應用上的基本運算.', 3, NULL, 0, 0, 0, ''),
(80, 'AEU', '電機工程學系', 'AEU0068', '電磁學（二）', '', '本科目旨在培養學生對於時變電磁場與電磁波的物理觀念及其在電子電機工程應用上的基本運算。', 3, NULL, 0, 0, 0, ''),
(81, 'AEU', '電機工程學系', 'AEU0069', '電磁學', '蔡政翰', '本科目旨在培養學生對於電、磁二者的物理觀念及其在電子電機工程應用上的基本運算。', 3, NULL, 0, 0, 1, '沒有限制'),
