(181, 'ATU', '美術學系', 'ATU0031', '水墨材料學', '', '', 0, NULL, 0, 0, 0, ''),
(182, 'ATU', '美術學系', 'ATU0032', '水墨構圖學', '', '', 2, NULL, 0, 0, 0, ''),
