INSERT INTO `course` (`id`, `department`, `chdepartment`, `code`, `chname`, `teacher`, `description`, `credit`, `fbreview`, `likeit`, `dislikeit`, `availability`, `grade`) VALUES
(1085, 'CEU', '公民教育與活動領導學系', 'CEU0354', '國家公園概論', '', '詳見教學大綱', 2, NULL, 0, 0, 0, ''),
(1086, 'CEU', '公民教育與活動領導學系', 'CEU0355', '探索教育概論', '', '', 2, NULL, 0, 0, 0, ''),
(1087, 'CEU', '公民教育與活動領導學系', 'CEU0356', '團體動力學', '劉秀嫚', '在人類追求慾望滿足的過程中，團體始終扮演相當重要的角色；但人們對團體卻普遍存在似是而非的觀點。事實上，團體並非靜態的概念，而是動態的歷程。團體在改變、團體中的個人在改變，團體中的人際關係也在改變。團體動力學是一門科際整合、利用科學方法探究發生在團體裡的各種行為與現象的知識領域，強調理論、研究與應用三者並重。基於此，本課程希冀藉教師講述讓學生了解團體動力學的基本概念與團體運作的原理原則，並藉由教師團體活動的帶領幫助學生對團體動力的發展及團體活動的設計、帶領與功能有更深刻的認識與體驗。另一方面則希冀學生將團體動力學的理論轉化為實務，與團體成員共同完成「團體動力主題活動設計與帶領」的實作。', 2, NULL, 0, 0, 1, '沒有限制'),
(1088, 'CEU', '公民教育與活動領導學系', 'CEU0357', '特殊童軍活動', '', '', 0, NULL, 0, 0, 0, ''),
(1089, 'CEU', '公民教育與活動領導學系', 'CEU0358', '共產主義研究', '', '', 0, NULL, 0, 0, 0, ''),
(1090, 'CEU', '公民教育與活動領導學系', 'CEU0359', '社會科課程設計', '', '', 0, NULL, 0, 0, 0, ''),
(1091, 'CEU', '公民教育與活動領導學系', 'CEU0360', '解說技巧概論', '', '', 0, NULL, 0, 0, 0, ''),
(1092, 'CEU', '公民教育與活動領導學系', 'CEU0363', '社會福利', '', '', 2, NULL, 0, 0, 0, ''),
(1093, 'CEU', '公民教育與活動領導學系', 'CEU0364', '道德教育原理', '李瑛明', '本課程是大學部學生對於倫理學有基礎概念之後，繼而引導在教育上如何進行家庭、學校與社會各面向的推動與實施之原理原則介紹。道德教育是與每個人生活息息相關的課題，也是當代公民應具有的教育理念，因而了解道德教育的理論基礎與實施方式，成為大學生重要且為師資生必修的課程。', 2, NULL, 0, 0, 1, ''),
(1094, 'CEU', '公民教育與活動領導學系', 'CEU0365', '社會問題', '', '', 0, NULL, 0, 0, 0, ''),
(1095, 'CEU', '公民教育與活動領導學系', 'CEU0366', '民主政治', '呂啟民／鄧毓浩', '本課程是延續政治學課程的重要學習領域。民主由來已久，但民主政治成為國家體制和普世價值，是近半世紀以來人類社會努力的結果。本課程強調裡論與實務並重，一方面講授民主政治的裡念和經驗，來瞭解民主政治的運作，闡揚民主的價值，以適應現代社會的生活；另一方面透過學生做「檢視臺灣政治問題」的報告，來發現臺灣社會潛在的問題，以\n培養學生關懷社會的意識，並轉化為公民的行動能力。', 2, NULL, 0, 0, 1, ''),
(1096, 'CEU', '公民教育與活動領導學系', 'CEU0367', '多元文化通論', '陳素秋', '透過閱讀、討論、分組報告、講授等方式，讓學生認識多元文化的相關理論和主張，以及與多元文化有關之議題，重點以台灣社會中常見之議題為主要對象。', 2, NULL, 0, 0, 1, '沒有限制'),
(1097, 'CEU', '公民教育與活動領導學系', 'CEU0368', '學生事務理論與實務', '', '本課程為大學部必修，以培養師培生未來進入中等學校，能更瞭解學校的學務行政工作，以及能更瞭解學生並幫助學生健全發展，同時也幫助非師培生未來考國家考試教育行政類科，擔負學生輔導有關工作，或者到大專院校擔任學務行政人員，負責大學學生事務工作。本課程為了增進學生理論與實務的結合，要求學生選擇相關單位服務至少12小時，採用將專業融入至服務中的服務學習模式。', 3, NULL, 0, 0, 0, ''),
(1098, 'CEU', '公民教育與活動領導學系', 'CEU0369', '活動規劃原理', '顏妙桂', '了解活動規畫的概念、理論與方法，學習活動方案設計、實施與評鑑，提升活動企畫力與執行力。', 2, NULL, 0, 0, 1, '沒有限制'),
(1099, 'CEU', '公民教育與活動領導學系', 'CEU0370', '領導概論', '', '瞭解領導的意義及影響領導行為的因素，學習領導理論與領導技巧，並能學習建構個人正確的領導觀並培養領導能力。', 2, NULL, 0, 0, 0, ''),
(1100, 'CEU', '公民教育與活動領導學系', 'CEU0371', '社會學習領域概論', '', '詳見教學大綱', 2, NULL, 0, 0, 0, ''),
(1101, 'CEU', '公民教育與活動領導學系', 'CEU0372', '社會學習領域課程與教學', '', '', 2, NULL, 0, 0, 0, ''),
(1102, 'CEU', '公民教育與活動領導學系', 'CEU0373', '公民教學專題', '', '', 2, NULL, 0, 0, 0, ''),
(1103, 'CEU', '公民教育與活動領導學系', 'CEU0374', '貨幣銀行學（一）', '', '', 2, NULL, 0, 0, 0, ''),
(1104, 'CEU', '公民教育與活動領導學系', 'CEU0375', '貨幣銀行學（二）', '', '', 0, NULL, 0, 0, 0, ''),
(1105, 'CEU', '公民教育與活動領導學系', 'CEU0376', '應用倫理學', '', '本課程是延續倫理學的基本概念與理論之後，進一步針對應用倫理學的各個範疇之加廣加深探究。其重點包括瞭解應用倫理學的各個範疇與相關理念，進而剖析與反思應用倫理學各個範疇的相關議題，以培養學習者對於應用倫理學的探究並運用於未來專業職場的知能。', 2, NULL, 0, 0, 0, ''),
(1106, 'CEU', '公民教育與活動領導學系', 'CEU0377', '合作學習', '', '「獨學而無友，則孤陋而寡聞」。「合作學習」有堅實的理論為基礎，雖然系統性的實徵研究迄今不過數十年，但卻已是擁有最多證據支持的教學良方，其具體成效包括提升學生的課業成就、自我概念、人際與認知技巧，以及改善班級氣氛等。本課程希冀透過教師的講述使學生了解「合作學習」的發展背景、理論基礎與教學原理；藉由教師示範教學幫助學生對各種合作學習法有更深刻的認識與體驗。另一方面則希冀學生從理論探索與實際經驗中構思課程主題，選擇最適合的合作學習法，與小組成員共同完成「合作學習教案設計」的實作。', 2, NULL, 0, 0, 0, ''),
(1107, 'CEU', '公民教育與活動領導學系', 'CEU0378', '野外生活技能', '陳永龍', '詳見教學大綱', 2, NULL, 0, 0, 1, '沒有限制'),
(1108, 'CEU', '公民教育與活動領導學系', 'CEU0379', '環境教育', '', '(1)環境教育的緣起與發展；\n(2)環境教育的原理； (3)環境教育的課程與方案； (4)環境教育實施與推廣。', 2, NULL, 0, 0, 0, ''),
(1109, 'CEU', '公民教育與活動領導學系', 'CEU0380', '服務學習活動設計與實施', '劉若蘭', '本課程旨在以講述和討論的方式，討論服務學習的理念、內容與實施，學習專業理論之應用並發現問題；以及透過影片欣賞，引導學生從弱勢觀點思考、建立同理心以追求關懷弱勢之社會正義。\n亦透過實地參與社區服務的方式進行，學生於每次服務後撰寫服務日誌，反思個人與社會的關係，形成利他互助之精神，並期待學生從服務中發展合作、穩定的人格特質。最後製作個人服務卷宗以整合課堂內外所學，並設計出服務學習之方案以檢核學生對於理論之轉化與應用的能力。', 2, NULL, 0, 0, 1, '沒有限制'),
(1110, 'CEU', '公民教育與活動領導學系', 'CEU0381', '自然體驗活動', '', '自然體驗活動是一門童軍教育課群的實作課程。在此課程中教學者介紹自然體驗活動的教育原理與設計原則，並利用套裝課程訓練學生如何設計與領導自然體驗活動。', 2, NULL, 0, 0, 0, ''),
(1111, 'CEU', '公民教育與活動領導學系', 'CEU0382', '休閒教育', '', '瞭解休閒的意義及影響休閒行為的因素，建構個人正確的休閒態度與培養休閒生活的能力、規劃休閒教育方案，透過小組合作實踐互助利他的精神', 2, NULL, 0, 0, 0, ''),
(1112, 'CEU', '公民教育與活動領導學系', 'CEU0383', '戶外探索設計與實施', '', '戶外探索理論介紹\n戶外探索課程設計與評估 戶外探索引導與反思 戶外探索實踐', 2, NULL, 0, 0, 0, ''),
(1113, 'CEU', '公民教育與活動領導學系', 'CEU0384', '童軍露營活動', '', '1.\n了解童軍露營活動的基本理論 2. 學習童軍露營活動的知識與技能 3. 學習童軍露營活動的設計與領導能力', 2, NULL, 0, 0, 0, ''),
(1114, 'CEU', '公民教育與活動領導學系', 'CEU0385', '童軍技能', '李素珍', '十八般武藝，做為一位童軍需要具備哪些呢？「童軍技能」是要培養學生童軍的本職學能，配合戶外的場所透過體驗學習方式，達到教與學的目的，最終能夠將童軍技能靈活運用於日常生活中。', 2, NULL, 0, 0, 1, '沒有限制'),
(1115, 'CEU', '公民教育與活動領導學系', 'CEU0386', '遊戲理論與實施', '', '詳見教學大綱', 2, NULL, 0, 0, 0, ''),
(1116, 'CEU', '公民教育與活動領導學系', 'CEU0387', '戶外遊憩領導技巧', '', '', 2, NULL, 0, 0, 0, ''),
(1117, 'CEU', '公民教育與活動領導學系', 'CEU0388', '休閒遊憩概論', '', '本課程除了教導休閒遊憩相關理論外，更納入台灣休閒遊憩實例和研究結果，以及未來發展方向和趨勢，使學習者更明瞭台灣休閒遊憩領域的產業、政府及學術界等三方面的努力成果，以及未來即將面臨的諸多挑戰。\n　　課程內容囊括休閒遊憩系統、遊憩資源、觀光遊憩資源之開發、休閒遊憩需求理論、休閒遊憩參與影響因子、風景遊憩區規劃、遊憩評估、遊樂區的經營管理、遊憩經濟分析、休閒遊憩行銷、遊憩活動的企劃、遊憩人力資源管理與問題、遊憩研究方法與未來方向等13個主題，針對休閒遊憩領域做有系統且完整的介紹，以教導本系學生對休閒遊憩領域之基本認識。', 2, NULL, 0, 0, 0, ''),
(1118, 'CEU', '公民教育與活動領導學系', 'CEU0389', '休閒服務管理', '', '', 2, NULL, 0, 0, 0, ''),
(1119, 'CEU', '公民教育與活動領導學系', 'CEU0390', '消費者保護法', '', '本課程係以消費者保護法之內容介紹予同學，使其對消費者保護法有所認識，並能從消費者、企業經營者及主管機關等不同立場理解此法之制度及規範精神，讓學生於日常生活中能夠對相關議題能有細緻、妥善的考慮及處理，因此課程內容包含以下幾個範疇：\n1.消費者保護法之立法目的 2.消費者保護法與其他法律間之關係 3.消費者保護法中有關消費者權益之規定 4.消費者保護團體之介紹\n5.消費者保護法中之行政監督 6.消費爭議之處理 7.重要實務案例', 3, NULL, 0, 0, 0, ''),
(1120, 'CEU', '公民教育與活動領導學系', 'CEU0391', '少年事件處理法', '', '', 0, NULL, 0, 0, 0, ''),
(1121, 'CEU', '公民教育與活動領導學系', 'CEU0392', '智慧財產權法', '', '法諺有云：「法律的生命不在邏輯，而在經驗」，因此，本課程之教授，不再要求背多少條文，而在習得智慧財產權法之主要精神及義理，建立正確之觀念，進而靈活運用，以謀求解決生活中具體之法律糾紛。故本課程將依智慧財產權法所涵蓋之主要內容，依序介紹著作權法及其相關概念。', 3, NULL, 0, 0, 0, ''),
(1122, 'CEU', '公民教育與活動領導學系', 'CEU0393', '政治文化與政治社會化', '', '自從1960 年Almond\n和Verba 出版了「公民文化」(Civic\nCulture)一書之後，「政治文化」就成為政治學中非常重要的領域。另一方面，從時間的軸向來看，一國民眾如何傳遞政治文化？個人如何形成特定的政治取向、態度與價值？則是「政治社會化」關注的課題。身為公領系的學生，瞭解個人如何型塑、建構與累積看待政治事務的眼光，將是必備的基礎知識。\n本課程的主要目標，將從集體與個體兩個面向來看待民眾政治價值、取向的形成、獲取、維持、強化與改變。老師期望同學們在修習本課程後，除了能掌握政治文化與政治社會化根本的理論脈絡外，更能進一步以台灣為個案，掌握我國民眾政治文化與社會化的特色。', 3, NULL, 0, 0, 0, ''),
(1123, 'CEU', '公民教育與活動領導學系', 'CEU0394', '地方政府與政治', '', '地方自治為民主政治之基礎，受各國政府重視，我國地方政府實施自治，亦累積相當寶貴之民主經驗。近年為因應改制直轄市及行政區劃，地方制度法有些修法及配套措施，而公共管理及行政學相關理論的大量引用，使得地方自治的概念，呈現「地方治理」、「跨域治理」、「多層次治理」「網路治理」、「全局治理」等多元樣貌。地方民眾自主意識漸高，政府遂扮演治理與服務的雙重角色。本課程自編講義(由本校moddle下載)，依時事議題設計活動，包括指標性案例討論、角色扮演辯論、地方選舉觀察報告與綜合活動等，並廣泛運用各類型教學資源輔助教學，包括出版品、網路教學資源與教學影片資源等。學期間規定有專題作業，其內容可依個人能力與興趣作適度的自我調整。期望同學配合課程進度預習與閱讀，並積極參與課堂討論、綜合活動等分組活動。', 2, NULL, 0, 0, 0, ''),
(1124, 'CEU', '公民教育與活動領導學系', 'CEU0395', '法治教育', '', '', 2, NULL, 0, 0, 0, ''),
(1125, 'CEU', '公民教育與活動領導學系', 'CEU0396', '個體經濟專題', '', '本課程主要為提升學生對於學習個體經濟學更深入議題的了解', 2, NULL, 0, 0, 0, ''),
(1126, 'CEU', '公民教育與活動領導學系', 'CEU0397', '總體經濟專題', '', '', 2, NULL, 0, 0, 0, ''),
(1127, 'CEU', '公民教育與活動領導學系', 'CEU0398', '教育經濟學', '', '從人力資本理論出發，透過對成本、收益、報酬率等指標的了解與粗估，用經濟學的工具對教育進行實證分析。', 2, NULL, 0, 0, 0, ''),
(1128, 'CEU', '公民教育與活動領導學系', 'CEU0399', '財務管理', '朱文增', '本課程介紹財務管理的基本觀念及分析工具，並著重理論與實務的結合應用。', 3, NULL, 0, 0, 1, '沒有限制'),
(1129, 'CEU', '公民教育與活動領導學系', 'CEU0400', '現代財經專題', '', '', 2, NULL, 0, 0, 0, ''),
(1130, 'CEU', '公民教育與活動領導學系', 'CEU0401', '財政理論與政策', '', '', 2, NULL, 0, 0, 0, ''),
(1131, 'CEU', '公民教育與活動領導學系', 'CEU0402', '企業管理概論', '', '', 0, NULL, 0, 0, 0, ''),
(1132, 'CEU', '公民教育與活動領導學系', 'CEU0403', '青少年發展與行為', '', '', 2, NULL, 0, 0, 0, ''),
(1133, 'CEU', '公民教育與活動領導學系', 'CEU0404', '學生權利概論', '', '', 2, NULL, 0, 0, 0, ''),
(1134, 'CEU', '公民教育與活動領導學系', 'CEU0405', '人格理論與教育', '', '', 2, NULL, 0, 0, 0, ''),
(1135, 'CEU', '公民教育與活動領導學系', 'CEU0406', '學生事務組織與行政', '', '', 0, NULL, 0, 0, 0, ''),
(1136, 'CEU', '公民教育與活動領導學系', 'CEU0407', '學校環境與青少年發展', '', '', 0, NULL, 0, 0, 0, ''),
(1137, 'CEU', '公民教育與活動領導學系', 'CEU0408', '校園法律問題', '', '', 2, NULL, 0, 0, 0, ''),
(1138, 'CEU', '公民教育與活動領導學系', 'CEU0409', '教育財政', '', '', 2, NULL, 0, 0, 0, ''),
(1139, 'CEU', '公民教育與活動領導學系', 'CEU0410', '社會科教學評量', '', '', 0, NULL, 0, 0, 0, ''),
(1140, 'CEU', '公民教育與活動領導學系', 'CEU0411', '綜合活動學習領域概論', '蔡居澤', '本課程為綜合活動領域學程核心課程之一，選修的學生透過此課程可以 1. 精進綜合活動學習領域的專業知識 2.\n嫻熟綜合活動學習領域活動設計教學的策略與技巧 3. 有效運用綜合活動學習領域教學相關資源 4 增進對綜合活動學習領域教學的信心與使命感\n5. 應用所學於未來教學生涯', 2, NULL, 0, 0, 1, '沒有限制'),
(1141, 'CEU', '公民教育與活動領導學系', 'CEU0412', '綜合活動學習領域設計與實施', '', '', 2, NULL, 0, 0, 0, ''),
(1142, 'CEU', '公民教育與活動領導學系', 'CEU0413', '性別教育', '', '本課程致力於培養學生具備覺知校園中性別偏見的能力，並能進一步具備實踐性別平等教學者之角色的行動能力。課程安排由性別社會建構談起，使學習者先瞭解性別作為社會結構，接著分別探討校園中的重要性別不平等議題，以及臺灣性別平等教育運動。', 2, NULL, 0, 0, 0, ''),
(1143, 'CEU', '公民教育與活動領導學系', 'CEU0414', '童軍技能─通訊、估測、製圖', '', '', 2, NULL, 0, 0, 0, ''),
(1144, 'CEU', '公民教育與活動領導學系', 'CEU0415', '童軍技能─星象與氣象', '', '「童軍技能—星象與氣象」是要連結童軍的進程訓練與專科考驗的內容，培養同學身處野外的基本生活能力，利用教室課堂的理論教學，配合戶外的場所，透過體驗學習或參觀訪問等方式，達到教與學的目的，最終能夠運用於戶外，並培養生存於21世紀的適應能力。', 2, NULL, 0, 0, 0, ''),
(1145, 'CEU', '公民教育與活動領導學系', 'CEU0427', '社會科學概論', '', '', 2, NULL, 0, 0, 0, ''),
(1146, 'CEU', '公民教育與活動領導學系', 'CEU0428', '環境社會學', '', '1.認識童軍教育研究專題之專有名詞。 2.解析童軍教育之理論與相關研究。 3.瞭解童軍教育相關理論專家與研究學者。\n4.分析童軍教育研究專題之相關資訊 5.綜合不同來源之童軍教育專題資訊，並加以評判。', 2, NULL, 0, 0, 0, ''),
(1147, 'CEU', '公民教育與活動領導學系', 'CEU0429', '社會發展', '', '本課程由傳統進入現代社會的分析為第一主軸，分析現代社會之現代性（modernity），並對比其與傳統社會之差異，接著分析後現代社會的重要轉變，並分別探討其對個人生活的影響，最後以全球觀點討論國際之間的社會發展不平等與衝突。', 2, NULL, 0, 0, 0, ''),
(1148, 'CEU', '公民教育與活動領導學系', 'CEU0430', '多元文化教育', '', '1.讓學生了解多元文化教育的相關議題，包括性別、族群、階級等。\n2.透過閱讀、討論、分組報告、講授、觀賞影片等方式，讓學生認識多元文化教育的相關理論和主張，以及與多元文化教育有關之議題。', 2, NULL, 0, 0, 0, ''),
(1149, 'CEU', '公民教育與活動領導學系', 'CEU0431', '個人財務規劃', '', '', 2, NULL, 0, 0, 0, ''),
(1150, 'CEU', '公民教育與活動領導學系', 'CEU0432', '企業管理', '', '介紹企業管理相關知識，包括作業管理、行銷管理、人力資源管理、 財務管理，以及探討策略上的管理、領導、企業危機管理等。', 3, NULL, 0, 0, 0, ''),
(1151, 'CEU', '公民教育與活動領導學系', 'CEU0433', '各國公民教育', '', '', 2, NULL, 0, 0, 0, ''),
(1152, 'CEU', '公民教育與活動領導學系', 'CEU0434', '民法（三）', '林安邦', '法諺有云：「法律的生命不在邏輯，而在經驗」，因此，本課程之教授，不再要求背多少條文，而在習得民法之主要精神及義理，建立正確之觀念，進而靈活運用，以謀求解決生活中具體之民事糾紛。蓋民法與個人日常生活最為密切，舉凡衣、食、住、行、育、樂，無一不是由一連串之法律行為（尤其是契約），架構當事人間之權利義務關係，欲謀求符合公平正義之和諧安寧，則必從履行個人之義務，且不讓自己之權利睡著出發，始能終底於成。故本課課程將在民法總則、財產法（債編、物權編）之基礎上，依循民法典之編排方式，依序介紹親屬編及繼承編。', 2, NULL, 0, 0, 1, '沒有限制'),
(1153, 'CEU', '公民教育與活動領導學系', 'CEU0435', '行政訴訟法', '劉恆妏', '詳見教學大綱', 2, NULL, 0, 0, 1, '沒有限制'),
(1154, 'CEU', '公民教育與活動領導學系', 'CEU0436', '政治學方法論', '', '', 3, NULL, 0, 0, 0, ''),
(1155, 'CEU', '公民教育與活動領導學系', 'CEU0437', '中西政治思想史', '', '', 3, NULL, 0, 0, 0, ''),
(1156, 'CEU', '公民教育與活動領導學系', 'CEU0438', '兒童及少年相關法律', '', '法諺有云：「法律的生命不在邏輯，而在經驗」，因此，本課程之教授，不再要求背多少條文，而在習得法律之主要精神及義理，建立正確之觀念，進而靈活運用，以謀求解決生活中具體之學生事件。蓋少年事件與未來為人師有極大關聯，故本課程將依少年事件處理法、兒童及少年性交易防制條例、兒童及少年福利與權益保障法，依序介紹少年事件處理法、兒童及少年性交易防制條例、兒童及少年福利與權益保障法等相關內容。', 3, NULL, 0, 0, 0, ''),
(1157, 'CEU', '公民教育與活動領導學系', 'CEU0439', '會計學', '', '詳見教學大綱', 3, NULL, 0, 0, 0, ''),
(1158, 'CEU', '公民教育與活動領導學系', 'CEU0440', '高等教育概論', '', '', 3, NULL, 0, 0, 0, ''),
(1159, 'CEU', '公民教育與活動領導學系', 'CEU0441', '大學生發展', '', '', 3, NULL, 0, 0, 0, ''),
(1160, 'CEU', '公民教育與活動領導學系', 'CEU0442', '大學生發展方案設計', '', '', 2, NULL, 0, 0, 0, ''),
(1161, 'CEU', '公民教育與活動領導學系', 'CEU0443', '童軍技能－攝影', '', '詳見教學大綱', 2, NULL, 0, 0, 0, ''),
(1162, 'CEU', '公民教育與活動領導學系', 'CEU0444', '團體動力與社團經營', '', '', 2, NULL, 0, 0, 0, ''),
(1163, 'CEU', '公民教育與活動領導學系', 'CEU0445', '高等教育組織與行政', '', '', 3, NULL, 0, 0, 0, ''),
(1164, 'CEU', '公民教育與活動領導學系', 'CEU0446', '教育法', '', '教育法，針對教育公權力行使之相關法律規範，主要是憲法與教育行政法規。以行政層級為區分，可分為中央、地方、學校等面向。以特定的學習階段或需求，可分為大學教育、中等教育、國民教育或原住民、特殊教育等。以主體區分，亦可分為校長、教師、學生與家長等面向。', 2, NULL, 0, 0, 0, ''),
(1165, 'CEU', '公民教育與活動領導學系', 'CEU0447', '高等教育學生事務實習', '', '', 2, NULL, 0, 0, 0, ''),
(1166, 'CEU', '公民教育與活動領導學系', 'CEU0448', '戶外探索教育概論', '', '本課程主要目的在探討戶外探索教育之原理，從事探索活動課程之設計，體驗戶外探索活動之進程發展，評估探索活動之效益。', 2, NULL, 0, 0, 0, ''),
(1167, 'CEU', '公民教育與活動領導學系', 'CEU0449', '解說教育概論', '', '本課程之目的在探討專業人員在從事解說工作及相關課程之相關理論與實務，內容包括：解說概論、解說媒體、解說員、室內解說、解說股施與器材、戶外教育與解說活動、遊客行為與特殊遊客解說、環境教育及解說規劃等。', 2, NULL, 0, 0, 0, ''),
(1168, 'CEU', '公民教育與活動領導學系', 'CEU0450', '戶外教育設施與規劃', '', '', 2, NULL, 0, 0, 0, ''),
(1169, 'CEU', '公民教育與活動領導學系', 'CEU0451', '營地經營與管理', '', '', 2, NULL, 0, 0, 0, ''),
(1170, 'CEU', '公民教育與活動領導學系', 'CEU0452', '活動專案企劃', '', '協助學生完成有創意的企劃點子，並完成可執行的企劃案，並執行評估企劃案。', 2, NULL, 0, 0, 0, ''),
(1171, 'CEU', '公民教育與活動領導學系', 'CEU0453', '活動專業實習', '', '', 3, NULL, 0, 0, 0, ''),
(1172, 'CEU', '公民教育與活動領導學系', 'CEU0454', '戶外遊憩行銷', '', '詳見教學大綱', 3, NULL, 0, 0, 0, ''),
(1173, 'CEU', '公民教育與活動領導學系', 'CEU0455', '露營基本裝備', '', '請詳見課程大綱。', 2, NULL, 0, 0, 0, ''),
(1174, 'CEU', '公民教育與活動領導學系', 'CEU0456', '露營生活技巧', '', '', 2, NULL, 0, 0, 0, ''),
(1175, 'CEU', '公民教育與活動領導學系', 'CEU0457', '戶外急救', '', '', 2, NULL, 0, 0, 0, ''),
(1176, 'CEU', '公民教育與活動領導學系', 'CEU0458', '露營技能', '', '', 2, NULL, 0, 0, 0, ''),
(1177, 'CEU', '公民教育與活動領導學系', 'CEU0459', '社團經營與領導', '', '社團的參與是在求學路上正式課程以外的重要學習機會，也是許多學生知識學習實踐、學習人際相處及學習經驗統整的管道與機會。在各級學校中，更是學務人員的重要工作之一。而在公民社會中，社團的參與與經營更是推動公民議題的重要方式。本系以培育未來學務及活動領導人才為主要目標，本課程將透過課堂中小組演練的方式，由社團成立到經營與管理，實際模擬演練社團的運作，以體會領導、溝通、創意、合作、自我探索的社團經營與管理內涵。', 2, NULL, 0, 0, 0, ''),
(1178, 'CEU', '公民教育與活動領導學系', 'CEU0460', '學生事務原理', '', '本課程目的在強化本系擔任公民教育及童軍教育、綜合活動等科目教師之就業競爭力，使修課學生能在擔任教師時能勝任學生事務工作。非師培生若修讀本科也有助於通過國家考試，進而能進入學校擔任學務工作行政人員，協助學校成功推動學生事務工作。', 2, NULL, 0, 0, 0, ''),
(1179, 'CEU', '公民教育與活動領導學系', 'CEU0461', '學生事務規劃與實施', '', '本課程目的在充實本校學生之學務知能，強化就業競爭力，使師培生能因修畢本課程在教師甄試時更受肯定；使非師培生則能因具備學務知能優勢，獲聘擔任私立高中職之專職學務行政人員，或有助通過國家考試，因而能擔任公立高中職之專職學務行政人員。\n課程重點在1.探究學生事務工作的教育目標如何落實，以達成促進學生學習發展的目標；2.配合12年國教的教育政策與目標，使學校能落實適性揚才，幫助學生健全發展、快樂學習。\n課程內容包含學生事務有關之活動方案規劃設計，及實施所需的溝通互動技巧、資源運用、行政倫理等學生事務規劃與執行有關專業知能，因此修畢課程之學生將能具備下列核心能力：', 2, NULL, 0, 0, 0, ''),
(1180, 'CEU', '公民教育與活動領導學系', 'CEU0462', '中學學生事務專業實習', '', '', 1, NULL, 0, 0, 0, ''),
(1181, 'CEU', '公民教育與活動領導學系', 'CEU0463', '學校環境與學生發展', '', '（一）\n探討環境與個人的互動關係 （二） 了解學校環境影響學生發展的相關概念 （三） 觀察體驗學校環境影響學生發展的情境與實例 （四）\n設計環境或方案促進學生學習與發展', 3, NULL, 0, 0, 0, ''),
(1182, 'CEU', '公民教育與活動領導學系', 'CEU0464', '正向管教原理與實施', '', '本課程以講述、討論、導讀報告、專書研究等方式，探討正向管教的理論與實務內容，學習及了解專業理論的應用。並透過深入閱讀正向管教相關論題，培養學生從不同角度面向探究成功的正向管教實施方式，以期待學生自我專業能力的養成與提升。透過報告與討論，與同儕產生思考辯論，以增進學生事務專業能力。', 2, NULL, 0, 0, 0, ''),
(1183, 'CEU', '公民教育與活動領導學系', 'CEU0465', '校園危機處理', '', '本課程以講述、討論、導讀報告、專書研究等方式，探討校園危機處理的理論與實務內容，學習及了解專業理論的應用。並透過深入閱讀校園危機處理實務相關論題，培養學生從不同角度面向探究成功的校園危機處理模式，以期待學生自我專業能力的養成與提升。透過報告與討論，與同儕產生思考辯論，以增進學生事務專業能力。', 2, NULL, 0, 0, 0, ''),
(1184, 'CEU', '公民教育與活動領導學系', 'CEU0466', '學生次文化', '', '本課程主要介紹學生次文化的意義與重要性，說明學校次文化的內涵、概念與性質，及學生次文化的特質、形成因素與研究發現，以及學生次文化的教育輔導。以期幫助有志於學校教育工作者更能掌握學生的學習、態度與行為，建立更良好校園風氣，促進學生學習與發展。', 2, NULL, 0, 0, 0, ''),
(1185, 'CEU', '公民教育與活動領導學系', 'CEU0467', '學生發展方案設計', '', '本課程主要針對學生上課外之課外活動時間應如何安排設計，如社團活動、服務學習、週末假日、寒暑假期等，應如何幫助各種興趣、能力、性向及社經背景不同的學生獲得全面發展。本課程適合願意關心學生發展的師培生修習，更適合願意擔任學務行政人員的非師培生修習。', 2, NULL, 0, 0, 0, ''),
(1186, 'CEU', '公民教育與活動領導學系', 'CEU0468', '學生事務組織與行政', '', '本課程主要是為非師培生開設，但也適合願意兼任學務行政工作的准教師修習。內容是介紹學校組織中學生事務工作的角色與功能，以幫助修課學生瞭解如何以學務行政之力，協助學生獲得最好的協助，以能在學校成功學習發展。', 3, NULL, 0, 0, 0, ''),
(1187, 'CEU', '公民教育與活動領導學系', 'CEU0469', '童軍露營活動（一）', '', '1.\n了解童軍露營活動的基本理論 2. 學習童軍露營活動的知識與技能 3. 學習童軍露營活動的設計與領導能力', 2, NULL, 0, 0, 0, ''),
(1188, 'CEU', '公民教育與活動領導學系', 'CEU0470', '童軍露營活動（二）', '陳永龍', '詳見課程大綱。', 2, NULL, 0, 0, 1, '沒有限制'),
(1189, 'CEU', '公民教育與活動領導學系', 'CEU0471', '綜合活動學習領域課程設計與實施', '', '', 2, NULL, 0, 0, 0, ''),
(1190, 'CEU', '公民教育與活動領導學系', 'CEU0472', '綜合活動學習領域教學與評量', '', '本課程為綜合活動領域學程核心課程之一，選修的學生透過此課程可以 1. 精進綜合活動學習領域的專業知識 2.\n嫻熟綜合活動學習領域活動設計教學的策略與技巧 3. 有效運用綜合活動學習領域教學相關資源 4.\n增進對綜合活動學習領域教學的信心與使命感 5. 應用所學於未來教學生涯', 2, NULL, 0, 0, 0, ''),
(1191, 'CEU', '公民教育與活動領導學系', 'CEU0473', '活動專業實務', '', '詳見課程大綱。', 3, NULL, 0, 0, 0, ''),
