(1193, 'CEU', '公民教育與活動領導學系', 'CEU0475', '少年事件相關法律', '', '', 3, NULL, 0, 0, 0, ''),
(1194, 'CEU', '公民教育與活動領導學系', 'CEU0476', '童軍技能─繩藝與手工藝', '', '', 2, NULL, 0, 0, 0, ''),
