(1196, 'CEU', '公民教育與活動領導學系', 'CEU0478', '戶外急救與安全管理', '', '', 2, NULL, 0, 0, 0, ''),
(1197, 'CEU', '公民教育與活動領導學系', 'CEU0479', '戶外活動（一）', '', '此次課程透過三次的實地戶外活動體驗，讓學生能夠對戶外活動產生興趣，並對於戶外活動中的活動領導理論能相互結合，也提高未來學習戶外技術與課程規劃與引導之先備經驗。', 2, NULL, 0, 0, 0, ''),
(1198, 'CEU', '公民教育與活動領導學系', 'CEU0480', '戶外體驗教育', '謝智謀', '', 2, NULL, 0, 0, 1, '沒有限制'),
(1199, 'CEU', '公民教育與活動領導學系', 'CEU0481', '冒險治療', '', '', 2, NULL, 0, 0, 0, ''),
