(3136, 'GCU', '圖文傳播學系', 'GCU0097', '傳播空間規劃', '', '', 2, NULL, 0, 0, 0, ''),
(3137, 'GCU', '圖文傳播學系', 'GCU0098', '傳播媒體設計', '', '', 0, NULL, 0, 0, 0, ''),
(3138, 'GCU', '圖文傳播學系', 'GCU0099', '媒體設備設計與運用', '', '', 2, NULL, 0, 0, 0, ''),
(3139, 'GCU', '圖文傳播學系', 'GCU0100', '商業設計', '', '', 0, NULL, 0, 0, 0, ''),
(3140, 'GCU', '圖文傳播學系', 'GCU0101', '網頁設計', '', '訓練學生對於使用各種網頁設計理論與技術解決關於數位圖文傳播科技的問題，並輔導學生從事網站開發與經營專題實作', 3, NULL, 0, 0, 0, ''),
(3141, 'GCU', '圖文傳播學系', 'GCU0102', '印刷版面規劃與設計', '', '', 0, NULL, 0, 0, 0, ''),
(3142, 'GCU', '圖文傳播學系', 'GCU0104', '視訊工程技術（一）', '王希俊', 'In this\ncourse, we focus on the theoretical understanding and computer\nhand-on practice of 1-D signal (audio), 2-D still image, and\nmulti-dimensional video data from the engineering point of\nview.', 3, NULL, 0, 0, 1, ''),
(3143, 'GCU', '圖文傳播學系', 'GCU0105', '視訊工程技術（二）', '', '讓學生具備視訊系統運作原理的基本知識，並加強互動裝置設計的訓練，以進行互動視訓系統的設計與實作。', 3, NULL, 0, 0, 0, ''),
(3144, 'GCU', '圖文傳播學系', 'GCU0106', '電腦動畫多媒體應用（一）', '周遵儒', '訓練學生對於使用各種進階的網際網路理論與技術解決關於數位圖文傳播科技的問題，並輔導學生從事相關課題的學術研究', 3, NULL, 0, 0, 1, ''),
(3145, 'GCU', '圖文傳播學系', 'GCU0107', '電腦動畫多媒體應用（二）', '', '', 3, NULL, 0, 0, 0, ''),
(3146, 'GCU', '圖文傳播學系', 'GCU0108', '影視原理與製作', '', '一、瞭解與具備影視節目發展與應用之知識、態度與能力。 二、瞭解與具備影視節目前瞻發展之關鍵知識、態度與能力。\n三、瞭解與具備影視節目創新發展與應用之知識、態度與能力。', 3, NULL, 0, 0, 0, ''),
(3147, 'GCU', '圖文傳播學系', 'GCU0109', '傳播心理學', '', '', 3, NULL, 0, 0, 0, ''),
(3148, 'GCU', '圖文傳播學系', 'GCU0110', '數位影像處理', '', '', 0, NULL, 0, 0, 0, ''),
(3149, 'GCU', '圖文傳播學系', 'GCU0111', '數位攝影', '', '1.瞭解數位相機原理與操作\n2.瞭解數位景觀營造與操作 3.瞭解仿生攝影與操作 4.瞭解數位全景拍攝 5.瞭解立體光柵與數位立體全景顯示', 2, NULL, 0, 0, 0, ''),
(3150, 'GCU', '圖文傳播學系', 'GCU0112', '線性代數', '', '', 2, NULL, 0, 0, 0, ''),
(3151, 'GCU', '圖文傳播學系', 'GCU0113', '離散數學', '', '', 0, NULL, 0, 0, 0, ''),
(3152, 'GCU', '圖文傳播學系', 'GCU0114', '電子出版系統', '', '', 0, NULL, 0, 0, 0, ''),
(3153, 'GCU', '圖文傳播學系', 'GCU0115', '電子報原理與實務', '', '能夠認識電子報的原理與現況\n能夠了解電子報的寫作與評論 能夠了解電子報的視效與編輯 能夠了解電子報的工具與發行 能夠建立資料蒐集與回饋 能夠建立互動式整合行銷\n能夠一次製作多種發行', 2, NULL, 0, 0, 0, ''),
(3154, 'GCU', '圖文傳播學系', 'GCU0116', '電子書原理與實務', '', '', 0, NULL, 0, 0, 0, ''),
