(3525, 'HEU', '健康促進與衛生教育學系', 'HEU0087', '醫療社會工作', '', '無此門課程', 0, NULL, 0, 0, 0, ''),
(3526, 'HEU', '健康促進與衛生教育學系', 'HEU0088', '醫療社會工作實習', '', '無此門課程', 0, NULL, 0, 0, 0, ''),
(3527, 'HEU', '健康促進與衛生教育學系', 'HEU0089', '團體發展與研究', '', '無此門課程', 0, NULL, 0, 0, 0, ''),
(3528, 'HEU', '健康促進與衛生教育學系', 'HEU0090', '家庭衛生教育', '', '無此門課程', 0, NULL, 0, 0, 0, ''),
(3529, 'HEU', '健康促進與衛生教育學系', 'HEU0091', '聽覺障礙兒童教材教法', '', '無此門課程', 0, NULL, 0, 0, 0, ''),
