INSERT INTO `course` (`id`, `department`, `chdepartment`, `code`, `chname`, `teacher`, `description`, `credit`, `fbreview`, `likeit`, `dislikeit`, `availability`, `grade`) VALUES
(3957, 'HGU', '人類發展與家庭學系', 'HGU0180', '會計學', '朱文增', '1.本課程係使同學學習有系統地記錄交易，並編製財務報表。本課程並介紹財務報表之要素及其衡量。此外，藉由課堂實例討論，使學生能瞭解不同財務報導方法對財務報表使用者決策之影響。\n2.\n我國將於2013年採用國際會計準則，本課程介紹國際會計準則及各國會計之差異，期使學生具有分析不同會計準則所編製財務報表之能力。', 2, NULL, 0, 0, 1, '二年級'),
(3958, 'HGU', '人類發展與家庭學系', 'HGU0181', '西餐烹調', '', '以基礎西式刀工、辛香料運用及西餐烹調法為架構，引領西餐烹調檢定丙級入門，激發對烹飪藝術興趣。', 2, NULL, 0, 0, 0, ''),
(3959, 'HGU', '人類發展與家庭學系', 'HGU0183', '服裝製作（三）', '', '', 0, NULL, 0, 0, 0, ''),
(3960, 'HGU', '人類發展與家庭學系', 'HGU0184', '服裝製作（四）', '', '', 2, NULL, 0, 0, 0, ''),
