(4387, 'HIU', '歷史學系', 'HIU0236', '後現代政經體制的變遷（二）', '', '', 2, NULL, 0, 0, 0, ''),
(4388, 'HIU', '歷史學系', 'HIU0237', '臺灣文化史（一）', '', '本課程之目標，一方面讓學生了解台灣文化之內容、變遷過程和特色，以增廣其台灣史知識；另一方面藉由文化反思批判，以奠定其有關台灣文化未來革故鼎新之基礎。', 2, NULL, 0, 0, 0, ''),
(4389, 'HIU', '歷史學系', 'HIU0238', '臺灣文化史（二）', '蔡淵洯', '本課程之目標，一方面讓學生了解台灣文化之內容、變遷過程和特色，以增廣其台灣史知識；另一方面藉由文化反思批判，以奠定其有關台灣文化未來革故鼎新之基礎。', 2, NULL, 0, 0, 1, '沒有限制'),
(4390, 'HIU', '歷史學系', 'HIU0239', '基礎聽講練習（一）', '', '', 1, NULL, 0, 0, 0, ''),
(4391, 'HIU', '歷史學系', 'HIU0240', '基礎聽講練習（二）', '', '', 1, NULL, 0, 0, 0, ''),
(4392, 'HIU', '歷史學系', 'HIU0241', '臺灣政治史（一）', '', '本課程藉由講述、論文研讀與討論，理解17世紀以降台灣政治發展及其變遷歷程，期能建立台灣主體意識，進而能主動關心台灣政治現勢，思索台灣的未來。', 2, NULL, 0, 0, 0, ''),
(4393, 'HIU', '歷史學系', 'HIU0242', '臺灣政治史（二）', '林欣宜', '本課程藉由講述、論文研讀與討論，理解17世紀以降台灣政治發展及其變遷歷程，期能建立台灣主體意識，進而能主動關心台灣政治現勢，思索台灣的未來。', 2, NULL, 0, 0, 1, '沒有限制'),
