(4602, 'ITU', '科技應用與人力資源發展學系', 'ITU0021', '汽車學（二）', '', '', 3, NULL, 0, 0, 0, ''),
(4603, 'ITU', '科技應用與人力資源發展學系', 'ITU0022', '工程製圖', '', '', 3, NULL, 0, 0, 0, ''),
