(4606, 'ITU', '科技應用與人力資源發展學系', 'ITU0025', '製造程序', '', '', 3, NULL, 0, 0, 0, ''),
(4607, 'ITU', '科技應用與人力資源發展學系', 'ITU0026', '模型製作', '簡佑宏 詹孝中', '本課程透過工廠實作使學生：1.瞭解模型在產業製作中的重要性與意義。2.習得產品模型之製作技巧。3.透過實際操作熟習各種素材之製作差異及實際操作機檯的技術及安全。', 3, NULL, 0, 0, 1, '一年級'),
(4608, 'ITU', '科技應用與人力資源發展學系', 'ITU0027', '印刷概論', '', '', 2, NULL, 0, 0, 0, ''),
(4609, 'ITU', '科技應用與人力資源發展學系', 'ITU0028', '電腦影像處理', '', '本課程旨在提供本系學生對於電腦影像處理與創作相關知能的學習', 3, NULL, 0, 0, 0, ''),
(4610, 'ITU', '科技應用與人力資源發展學系', 'ITU0029', '電腦桌上排版', '', '', 2, NULL, 0, 0, 0, ''),
