(4791, 'MAU', '數學系', 'MAU0053', '應用數學專題（一）', '', '', 3, NULL, 0, 0, 0, ''),
(4792, 'MAU', '數學系', 'MAU0054', '應用數學專題（二）', '', '', 3, NULL, 0, 0, 0, ''),
(4793, 'MAU', '數學系', 'MAU0055', '數理統計', '', '', 0, NULL, 0, 0, 0, ''),
(4794, 'MAU', '數學系', 'MAU0056', '數理統計（一）', '張少同', '本課程是研究隨機現象統計規律的一門學科。它提供統計研究與應用的理論基礎。內容包括常用分配之介紹、參數之估計及檢定、估計量之充分性、一致性、有效性等，期能建構推論統計學的理論基礎。', 3, NULL, 0, 0, 1, '三年級'),
(4795, 'MAU', '數學系', 'MAU0057', '數理統計（二）', '', '本課程是研究隨機現象統計規律的一門學科，是數學重要的一個分支。它提供統計研究與應用的理論基礎。內容包括常用分配之介紹、參數之估計及檢定、估計量之充分性、一致性、有效性等，期能建構推論統計學的理論基礎。', 3, NULL, 0, 0, 0, ''),
(4796, 'MAU', '數學系', 'MAU0058', '機率論（一）', '', '', 3, NULL, 0, 0, 0, ''),
(4797, 'MAU', '數學系', 'MAU0059', '機率論（二）', '', '', 3, NULL, 0, 0, 0, ''),
(4798, 'MAU', '數學系', 'MAU0060', '資料處理', '', '', 3, NULL, 0, 0, 0, ''),
(4799, 'MAU', '數學系', 'MAU0061', '線性規劃', '', '本課程介紹日常生活中實際問題的線性規劃模型。並將學習一套有系統的方法（單體法,Simplex\nmethod）來求解各類型的線性規劃問題。', 3, NULL, 0, 0, 0, ''),
(4800, 'MAU', '數學系', 'MAU0062', '應用統計方法（一）', '', '', 3, NULL, 0, 0, 0, ''),
(4801, 'MAU', '數學系', 'MAU0063', '應用統計方法（二）', '', '', 3, NULL, 0, 0, 0, ''),
(4802, 'MAU', '數學系', 'MAU0064', '作業研究', '', '本課程介紹作業研究的發展過程與實際應用，同時讓學生熟悉一些電腦軟體的操作與模擬來解決實際問題。', 3, NULL, 0, 0, 0, ''),
(4803, 'MAU', '數學系', 'MAU0065', '電子計算機概論', '', '本課程介紹計算機與網路基本概念，包括電腦軟硬體設備、作業系統、資料儲存型式、程式語言等，為學習計算機及其應用的基本科目。', 3, NULL, 0, 0, 0, ''),
(4804, 'MAU', '數學系', 'MAU0066', '佇候理論', '', '', 3, NULL, 0, 0, 0, ''),
(4805, 'MAU', '數學系', 'MAU0067', '組合數學', '', '', 3, NULL, 0, 0, 0, ''),
(4806, 'MAU', '數學系', 'MAU0068', '組合導論', '', '', 0, NULL, 0, 0, 0, ''),
(4807, 'MAU', '數學系', 'MAU0069', '非線型規劃', '', '', 0, NULL, 0, 0, 0, ''),
(4808, 'MAU', '數學系', 'MAU0070', '幾何學', '', '', 0, NULL, 0, 0, 0, ''),
(4809, 'MAU', '數學系', 'MAU0071', '代數學', '', '本課程讓學生瞭解群、環、體的基本理論，以作為學生未來修習其他進階課程的基礎。', 3, NULL, 0, 0, 0, ''),
(4810, 'MAU', '數學系', 'MAU0072', '代數（一）', '', '', 0, NULL, 0, 0, 0, ''),
(4811, 'MAU', '數學系', 'MAU0073', '代數（二）', '', '', 0, NULL, 0, 0, 0, ''),
