(4813, 'MAU', '數學系', 'MAU0075', '圖形導論', '', '', 0, NULL, 0, 0, 0, ''),
(4814, 'MAU', '數學系', 'MAU0076', '近世代數', '', '', 0, NULL, 0, 0, 0, ''),
(4815, 'MAU', '數學系', 'MAU0077', '數學史', '', '本課程期望透過對於數學發展與人文活動的歷史來豐富學生的思考，使學生對數學知識的本質有進一步的瞭解，並期學生能應用數學史於數學教育中。', 3, NULL, 0, 0, 0, ''),
(4816, 'MAU', '數學系', 'MAU0078', '數學教學與評量', '', '', 0, NULL, 0, 0, 0, ''),
(4817, 'MAU', '數學系', 'MAU0079', '整數論', '', '', 0, NULL, 0, 0, 0, ''),
(4818, 'MAU', '數學系', 'MAU0080', '機率論', '', '', 0, NULL, 0, 0, 0, ''),
(4819, 'MAU', '數學系', 'MAU0081', '優選導論', '', '', 0, NULL, 0, 0, 0, ''),
(4820, 'MAU', '數學系', 'MAU0082', '優選理論', '', '本課程介紹一般最佳化問題的結構，並簡介最佳題解的優選條件與相關演算法。', 3, NULL, 0, 0, 0, ''),
(4821, 'MAU', '數學系', 'MAU0083', '計算機語言', '', '', 0, NULL, 0, 0, 0, ''),
(4822, 'MAU', '數學系', 'MAU0084', '計算機語言程式及實習', '', '', 0, NULL, 0, 0, 0, ''),
(4823, 'MAU', '數學系', 'MAU0085', '數學與電腦', '', '', 0, NULL, 0, 0, 0, ''),
