(4832, 'MAU', '數學系', 'MAU0094', '實變數函數導論', '', '介紹Riemann-Stieltjes積分、Lebesgue測度、Lebesgue積分，並比較Lebesgue積分與Riemann積分之異同，使學生熟悉近代分析的一些基本技巧。', 3, NULL, 0, 0, 0, ''),
(4833, 'MAU', '數學系', 'MAU0096', '電腦演算法', '', '', 3, NULL, 0, 0, 0, ''),
(4834, 'MAU', '數學系', 'MAU0097', '電腦演算法分析與設計', '', '', 0, NULL, 0, 0, 0, ''),
(4835, 'MAU', '數學系', 'MAU0098', '資料結構與應用', '', '', 3, NULL, 0, 0, 0, ''),
(4836, 'MAU', '數學系', 'MAU0099', '資料結構', '', '', 3, NULL, 0, 0, 0, ''),
(4837, 'MAU', '數學系', 'MAU0100', '微分方程導論', '', '微分方程式大多起源於物理、工程、生命科學等領域。本課程提供學生有關微分方程之基本知識，其內容主要介紹階微分方程的來源，各類解法及應用。', 3, NULL, 0, 0, 0, ''),
(4838, 'MAU', '數學系', 'MAU0101', '數學概念發展（一）', '', '', 0, NULL, 0, 0, 0, ''),
(4839, 'MAU', '數學系', 'MAU0102', '數學概念發展（二）', '', '', 0, NULL, 0, 0, 0, ''),
(4840, 'MAU', '數學系', 'MAU0103', '可微分流形（一）', '', '', 0, NULL, 0, 0, 0, ''),
(4841, 'MAU', '數學系', 'MAU0104', '可微分流形（二）', '', '', 0, NULL, 0, 0, 0, ''),
