(4843, 'MAU', '數學系', 'MAU0106', '微分幾何（一）', '', '本課程主要訓練學生瞭解曲線及曲面在三維歐氏空間的一些基本性質。', 3, NULL, 0, 0, 0, ''),
(4844, 'MAU', '數學系', 'MAU0107', '微分幾何（二）', '林俊吉／陳瑞堂', '本課程主要訓練學生瞭解曲線及曲面在三維歐氏空間的一些基本性質。', 3, NULL, 0, 0, 1, '三年級'),
(4845, 'MAU', '數學系', 'MAU0108', '數學解題活動（一）', '', '', 0, NULL, 0, 0, 0, ''),
