(5982, 'PEU', '體育學系', 'PEU0233', '羽球（四）', '蔡虔祿', '', 1, NULL, 0, 0, 1, ''),
(5983, 'PEU', '體育學系', 'PEU0234', '羽球（五）', '', '一、熟練羽球各項基本動作要領。 二、熟練羽球裁判法及規則。 三、了解羽球教學法、享受羽球運動樂趣。', 1, NULL, 0, 0, 0, ''),
(5984, 'PEU', '體育學系', 'PEU0235', '羽球（六）', '徐孟達', '', 1, NULL, 0, 0, 1, '四年級'),
(5985, 'PEU', '體育學系', 'PEU0236', '桌球（四）', '', '', 1, NULL, 0, 0, 0, ''),
(5986, 'PEU', '體育學系', 'PEU0237', '桌球（五）', '', '桌球（五）課程目的，除了延續桌球（四）課程內容要求，更加全方面提升學生對於桌球運動特性與理論的掌控與理解，一方面增進學生指導講述與示範的能力；另一方面加強學生桌球競賽的實戰能力，以及鑑賞桌球比賽的分析能力。', 1, NULL, 0, 0, 0, ''),
(5987, 'PEU', '體育學系', 'PEU0238', '桌球（六）', '林靜萍', '', 1, NULL, 0, 0, 1, '四年級'),
(5988, 'PEU', '體育學系', 'PEU0239', '水上安全與救生（一）', '', '1', 1, NULL, 0, 0, 0, ''),
(5989, 'PEU', '體育學系', 'PEU0240', '水上安全與救生（二）', '', '', 1, NULL, 0, 0, 0, ''),
